SELECT id, video_id As videoId, title, video_time As videoTime, thumbnail, type, description, view_count AS viewCount, comment_count AS commentCount, title_length AS titleLength, description_length AS descriptionLength
FROM video_info
WHERE id >= #{start} AND id < #{end}

SELECT id, video_id As videoId, author, time, comment, comment_length AS commentLength, sentiment, magnitude, chat, chat_length AS chatLength, senti_update AS sentiUpdate, category, confidence, cate_update AS cateUpdate FROM comment WHERE video_id = #{videoId}
AND senti_update IS NULL
LIMIT #{limit}

